import { userRef, useEffect, useState } from 'react';

const Alert = props => {
    const type = props.type;
    return(
        <>
            <div className="alertShadow"></div>
            <div className="alerContainer">
                <h1>{props.title}</h1>
                <p>{props.body}</p>
                <ul>
                    {type==="confirmation"?(<button>Cancelar</button>):""} 
                    <button>Aceptar</button>
                </ul>
            </div>
        </>
    )
}
export default Alert;
