import ProfilePic from '../assets/profile.png';

const ProfileHeader = props => {
	let pic = props.user.providerData[0].providerId === "facebook.com" ? props.user.photoURL + "?height=600" : props.user.photoURL;
	return (
		<>
			<div id="info">

				<div>
					<h1>Mi cuenta</h1>
					<h2>{props.user.displayName}</h2>
					<span><i className="material-icons">email</i> {props.user.email.substr(0, props.user.email.indexOf(".com"))}</span>
				</div>

				<div id="profilePic">
					<img src={ProfilePic} alt='Profile Placeholder' />
					<img src={pic} alt='Profile' />
				</div>

			</div>
			<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320"><path fill="var(--primary)" fillOpacity="1" d="M0,160L48,176C96,192,192,224,288,234.7C384,245,480,235,576,202.7C672,171,768,117,864,112C960,107,1056,149,1152,186.7C1248,224,1344,256,1392,272L1440,288L1440,0L1392,0C1344,0,1248,0,1152,0C1056,0,960,0,864,0C768,0,672,0,576,0C480,0,384,0,288,0C192,0,96,0,48,0L0,0Z"></path></svg>
			<style jsx>{`
	   #info{
	      background:var(--primary);
	      padding:20px 20px 0 20px;
	      color:var(--backgrounds);
	      position:relative;
	      z-index:2;
	      width:100%;
	      display:flex;
	      justify-content:space-between;
	      align-items:center;
	   }
	   h1{
	      font-size:0.9em;
	   }
	   h2{
  	      white-space: nowrap;
  	      overflow: hidden;
  	      text-overflow:' ';
	      color:var(--backgrounds);
	   }
	   span{
	      font-size:1.1em;
	      opacity:0.8;
	      display:flex;
	      align-items:center;
	   }
	   span i{
	      margin-right:5px;
	      font-size:1em;
	   }
	   svg{
	      margin-top:-30px;
	   }
	   #profilePic{
	      width:130px;
	      height:130px;
	      position:relative;
	      margin-bottom:-80px;
	      border-radius:100%;
	      overflow:hidden;
	   }
	   #profilePic img{
	      width:100%;
	      position:absolute;
	      top:0;
	      right:0;
	      color:transparent;
	   }
	 `}</style>
		</>
	)
}

export default ProfileHeader;
