const Alert = props => {
    const type = props.type;
    return (
        <div className="alertContainer">
            <div className="alertShadow"></div>
            <h1>{props.title}</h1>
            <p>{props.body}</p>
            <ul>
                {type === "confirmation"?<button>Cancelar</button>:""}
                <button>Aceptar</button>
            </ul>
            <style jsx>{`
                .alertContainer{
                    width:100%;
                    height:100vh;
                    position:fixed;
                    top:0;
                    left:0;
                    z-index:100;
                }
                .alertContainer > .alertShadow{
                    width:100%;
                    height:100%;
                    position:absolute;
                    top:0;
                    left:0;
                    bakcground:rgba(0,0,0,.4);
                    z-index:1;
                }
            `}</style>
        </div>
    )
}
export default Alert;
