const Alert = props => {
    const type = props.type;
    return (
        <div className="alertContainer">
            <div className="alertShadow"></div>
            <div id="alertContent">
                <h1>{props.title}</h1>
                <p>{props.body}</p>
                <ul>
                    {type === "confirmation" ? <li><button className="waves waves-dark">Cancelar</button></li> : ""}
                    <li><button className="waves waves-dark">Cancelar</button></li>
                    <li><button className="waves waves-dark primary">Aceptar</button></li>
                </ul>
            </div>
            <style jsx>{`
                @keyframes show{
                    from{opacity:0}
                    to{opacity:1}
                }
                .alertContainer{
                    width:100%;
                    height:100vh;
                    position:fixed;
                    top:0;
                    left:0;
                    z-index:100;
                    animation:show 0.4s ease-in-out 1;
                }
                .alertContainer > .alertShadow{
                    width:100%;
                    height:100%;
                    position:absolute;
                    top:0;
                    left:0;
                    background:rgba(0,0,0,.4);
                    z-index:1;
                }
                .alertContainer #alertContent{
                    position:absolute;
                    top:50%;
                    left:50%;
                    background:var(--backgrounds);
                    padding:20px;
                    width:calc(100% - 40px);
                    max-width:400px;
                    transform:translate(-50%, -50%);
                    z-index:2;
                    box-shadow:0 0 10px rgba(0,0,0,.3);
                    border-radius:10px;
                }
                ul{
                    display:flex;
                    justify-content:flex-end;
                }
                ul li:nth-child(1) button{
                    color:var(--text);
                }
                ul li:nth-child(2) button{
                    color:var(--backgrounds);
                }
                h1{
                    color:var(--text);
                }
                p{
                    margin-bottom:10px;
                }
            `}</style>
        </div>
    )
}
export default Alert;
